sources.sass\\components\\_header_modal.scss:
.header-backdrop {
  position: fixed;
  top: 0px;
  left: 0px;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.2);
  opacity: 1;
  visibility: visible;
  z-index: 11;

  transform: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1),
    visibility 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.header-backdrop.is-hidden {
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
}
.header-backdrop.is-hidden.modal {
  transform: translate(-50% -50%) scale(0.9);
}

/*========================= Modal form ===================================*/
.modal {
  position: absolute;
  display: flex;
  margin: 0 auto;
  flex-direction: column;
  width: 450px;
  height: 609px;
  left: 50%;
  top: 50%;
  background: #ffb8ca;
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  transform: translate(-50%, -50%) scale(1);
  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
  padding: 40px;

  @media screen and (max-width: 479px) {
    width: 95vw;
    left: calc(100vw / 2);
  }
  @media screen and (min-width: 1200px) {
    width: 528px;
    height: 581px;
  }
}

.modal__close-button {
  position: fixed;
  display: flex;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  top: 8px;
  right: 8px;
  padding: 0;
  margin: 0;
  fill: #ffffff;
  justify-content: center;
  align-items: center;
  background: #ffb8ca;
  border: 1px solid #d41443;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    cursor: pointer;
    fill: #d41443;
    outline: none;
  }
}

.modal__heading {
  font-size: 20px;
  line-height: 1.5;
  letter-spacing: 0.03em;
  text-align: center;
  color: #d41443;

  @media screen and (min-width: 1200px) {
    line-height: 1.15;
  }
}
.modal__form {
  margin-top: 12px;
  background-color: #ffb8ca;
}
.modal__form-item {
  display: block;
  position: relative;
  font-size: 12px;
  line-height: 1.1;
  letter-spacing: 0.01em;
  color: #ffffff;
  transition-property: border;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__form-item:nth-child(-n + 3) {
  margin-bottom: 10px;
}
.modal__form-item:nth-child(4) {
  margin-bottom: 20px;
}
.modal__form-item-input {
  width: 100%;
  height: 40px;
  border: 1px solid #d41443;
  border-radius: 4px;
  margin-top: 4px;
  padding: 0px 12px 0px 42px;
  transition-property: fill, border;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__form-item-input--msg {
  width: 100%;
  height: 128px;
  resize: none;
  font-family: inherit;
  padding: 12px 16px;
  font-size: 14px;
  line-height: 1.14;
  &::placeholder {
    color: #ffb8ca;
  }

  @media screen and (min-width: 1200px) {
    font-size: 12px;
    line-height: 1.17;
  }
}
.modal__form-item-icon {
  position: absolute;
  top: 50%;
  left: 12px;
  fill: #ffb8ca;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal__form-cb {
  display: flex;
  align-items: center;
  font-size: 12px;
  line-height: 1.1;
  letter-spacing: 0.03em;
  color: #d41443;
  margin-bottom: 30px;
  justify-content: center;
  &::before {
    content: '';
    width: 16px;
    height: 15px;
    border: 2px solid #d41443;
    border-radius: 2px;
    margin-right: 7px;
  }
  @media screen and (min-width: 1200px) {
    font-size: 14px;
    line-height: 1.71;
  }
}
.modal__form-cb a {
  text-decoration: underline;
  color: #d41443;
  margin-left: 5px;
}

.modal__form-checkbox:checked + .modal__form-cb::before {
  background-color: #d41443;
  background-image: url(../images/svg/icon-header-check.svg);
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  border: 2px solid #d41443;
  transition-property: border;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__form-checkbox:focus + .modal__form-cb::before {
  transition-property: border;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__form-checkbox:focus + .modal__form-cb::before {
  border: 2px solid #d41443;
}
.modal__form-checkbox:hover + .modal__form-cb::before {
  transition-property: border;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
}
.modal__form-checkbox:hover + .modal__form-cb::before {
  border: 2px solid #d41443;
}
.modal__form-checkbox {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  border: 0;
  padding: 0;
  clip: rect(0 0 0 0);
  overflow: hidden;
}
.modal__form-cb-button {
  margin: 0 0 0 calc((100% - 200px) / 2);
  width: 209px;
  height: 44px;
  background: #ffffff;
  border-radius: 22px;
  border: 0;
  font-family: 'DM Sans', sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.31;
  color: #d41443;
  align-items: center;
  text-align: center;
  &:hover,
  &:focus {
    background: #ecebeb;
  }
}

.modal__form-item-input:hover,
.modal__form-item-input:focus {
  outline: none;
  border: 1px solid #d41443;
}
.modal__form-item:hover {
  fill: #d41443;
}
.modal__form-item-input:focus + svg {
  fill: #d41443;
}

sources.sass\\layout\\_header.scss:
a {
  text-decoration: none;
  margin: 0;
  padding: 0;
}
.header-container {
  padding: 0 15px;
  width: 100%;
  @media screen and (min-width: 768px) {
    position: relative;
    padding: 0 34px 0 34px;
    width: 768px;
    margin: 0 auto;
  }
  @media screen and (min-width: 1280px) {
    padding: 0 115px 0 46px;
    width: 1280px;
    margin: 0 auto;
  }
}
.header-menu__link,
.header-navbar__link {
  font-weight: bold;
  font-size: 14px;
  line-height: 1.29;
  color: #ffffff;
  &:hover,
  &:focus {
    color: #d41443;
  }
}

.header {
  display: flex;
  position: relative;
  background-color: #ffb8ca;
  justify-content: space-between;
}

.header-navbar-container {
  padding: 6px 0 0 0;

  @media screen and (min-width: 768px) {
    padding: 37px 0 0 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
}

@media screen and (min-width: 1280px) {
  .header-navbar,
  .header-navbar__boxes {
    display: flex;
  }
  .header-navbar__item:not(:last-child) {
    margin-right: 35px;
  }

  .header-navbar__boxes {
    padding: 0 110px;
    margin: auto;
    z-index: 10;
  }
}

.header-menu__item {
  padding: 11px 0 13px 21px;
  border-top: 1px solid #e18298;
  &:last-child {
    border-bottom: 1px solid #e18298;
  }
}

@media screen and (max-width: 767px) {
  .header-navbar__buy-button {
    display: none;
  }
}
@media screen and (max-width: 1279px) {
  .header-navbar__boxes {
    display: none;
  }
}

// Модальное меню
.header-menu__container {
  position: fixed;
  // display: none;
  opacity: 0;
  pointer-events: auto;
  visibility: visible;
  transform: translateX(100%);
  transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  // transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1), opacity 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.header-menu__container.is-open {
  display: block;
  position: fixed;
  width: 250px;
  height: 100vh;
  background-color: #e17992;
  right: 0;
  z-index: 6;
  opacity: 1;
  transform: translateX(0);
  transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1),
    visibility 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

// Кнопка "Бургер"
.header-menu__icon-cross {
  fill: #ffffff;
}
.header-menu__burger-batton {
  position: absolute;
  top: 25px;
  right: 21px;
  z-index: 7;
  @media screen and (min-width: 768px) {
    top: 52px;
    right: 224px;
  }
}
.header-menu__burger-batton.is-open {
  position: fixed;
  @media screen and (min-width: 768px) {
    top: 25px;
    right: 21px;
  }
}

.header-menu__burger-batton .header-menu__battons .header-menu__icon-cross {
  display: none;
}
.header-menu__burger-batton.is-open .header-menu__battons .header-menu__icon-cross {
  display: block;
}
.header-menu__burger-batton.is-open .header-menu__icon-menu {
  display: none;
}

@media screen and (min-width: 1280px) {
  .header-menu__battons {
    display: none;
  }
  .header-menu__container.is-open {
    display: none;
  }
}

// кнопка "Купить"
.header-menu__buy-button-arrow,
.header-navbar__buy-button-arrow {
  display: inline-block;
}
.header-navbar__buy-button-arrow {
  margin-left: 12px;
}
.header-menu__buy-button-arrow {
  margin-left: 16px;
  @media screen and (min-width: 768px) {
    margin-left: 14px;
  }
}

.header-menu__buy-button {
  width: 209px;
  height: 44px;
  left: 21px;
  top: 60px;
  margin: 60px 0 32px 21px;
  background: #ffffff;
  border-radius: 22px;
  border: 0;
  font-family: 'DM Sans', sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.31;
  color: #d41443;
  align-items: center;
  text-align: center;
  &:hover,
  &:focus {
    background: #ecebeb;
  }

  @media screen and (min-width: 768px) {
    width: 186px;
    margin: 60px 0 32px 34px;
  }
}

.header-navbar__buy-button.is-open {
  display: none;
  @media screen and (min-width: 1280px) {
    display: block;
  }
}
.header-navbar__buy-button {
  width: 160px;
  height: 44px;
  background: #ffffff;
  border-radius: 22px;
  border: 0;
  z-index: 5;
  font-family: 'DM Sans', sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.31;
  color: #d41443;
  align-items: center;
  text-align: center;
  &:hover,
  &:focus {
    background: #ecebeb;
  }
}

sources.sass\\_hero.scss:
@import './utils/viriables';
.hero {
  margin-left: 0;
  width: 100%;
  background-color: #ffb8ca;
  height: 502px;
  margin-top: -56px;
  @media screen and (min-width: 768px) {
    height: 399px;
    margin-top: -87px;
  }
  @media screen and (min-width: 1280px) {
    height: 665px;
    margin-top: -87px;
  }
}

.hero__container {
  height: 100%;
  position: relative;
  margin: 0 auto;
  @media screen and (min-width: 320px) {
    background-position: 70% 0, 80% 0;
    background-size: auto;
    background-position: 70% 100%, 100% 55%;
  }

  @media screen and (min-width: 480px) {
    width: 480px;
    background-position: 45% 100%, 50% 60%;
  }
  @media screen and (min-width: 768px) {
    width: 768px;
  }
  @media screen and (min-width: 1280px) {
    width: 1280px;
    height: 665px;
    padding-left: 0;
  }
}

.hero__container-arrow {
  position: absolute;
  cursor: pointer;
  z-index: 1;
  @media screen and (min-width: 768px) {
    background-image: url(../images/tab/arrow.svg);
    width: 18px;
    height: 18px;
    background-size: 18px;
    background-repeat: no-repeat;
    top: 347px;
    left: 154px;
  }
  @media screen and (min-width: 1280px) {
    width: 26px;
    height: 26px;
    background-image: url(../images/tab/arrow.svg);
    background-size: 26px;
    background-repeat: no-repeat;
    top: 583px;
    left: 295px;
  }
}

.hero__icecream-container-320 {
  background-image: url(../images/tel/hero-tel-1x.png), url(../images/tel/Ellipse-tel-1x.png);
  @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
    background-image: url(../images/tel/hero-tel-2x.png), url(../images/tel/Ellipse-tel-2x.png);
  }
  @media screen and (min-width: 320px) {
    height: 502px;
    background-repeat: no-repeat;
    background-size: 171px 410px, 285px;
    // background-position: 70% 0, 80% 0;
    background-position: 80% 100%, 100% 55%;
  }
  @media screen and (min-width: 480px) {
    display: none;
  }
}

.hero__icecream-container-480 {
  @media screen and (min-width: 480px) {
    background-image: url(../images/tel/hero-tel-1x.png), url(../images/tel/Ellipse-tel1-1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/tel/hero-tel-2x.png), url(../images/tel/Ellipse-tel1-2x.png);
    }
    width: 480px;
    height: 502px;
    background-position: 45% 100%, 50% 60%;
    background-repeat: no-repeat;
    background-size: 171px 410px, 346px 346px;
  }
  @media screen and (min-width: 768px) {
    display: none;
  }
}

.hero__icecream-container-768 {
  @media screen and (min-width: 768px) {
    background-size: auto, auto;
    background-position: center;
    background-repeat: no-repeat;
    background-image: url(../images/tab/hero-icecream-tab-1x.png),
      url(../images/tel/Ellipse-tel1-1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/tab/hero-icecream-tab-2x.png),
        url(../images/tel/Ellipse-tel1-2x.png);
    }
    background-size: 143px 336px, 324px 322px;
    height: 399px;
    background-position: 60% 100%, 67% 50%;
  }
  @media screen and (min-width: 1280px) {
    display: none;
  }
}

.hero__icecream-container-1280 {
  @media screen and (min-width: 1280px) {
    background-size: 236px 557px, 538px 538px;
    width: 1280px;
    background-repeat: no-repeat;
    background-image: url(../images/desk/hero-icecream-desk-1x.png),
      url(../images/desk/Ellipse-desk-1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/desk/hero-icecream-desk-2x.png),
