.hero_title-container {
  min-width: 218px;
  @media screen and (min-width: 320px) {
    position: absolute;
    left: 20px;
    bottom: 160px;
  }
  @media screen and (min-width: 768px) {
    position: absolute;
    left: 34px;
    bottom: 173px;
  }
  @media screen and (min-width: 1280px) {
