  @media screen and (min-width: 1280px) {
    background-image: url(../images/desk/hero-girl-desk-1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/desk/hero-girl-desk-2x.png);
    }
    background-repeat: no-repeat;
    position: absolute;
    background-size: 293px 232px;
