  @media screen and (min-width: 1280px) {
    background-image: url(../images/desk/hero-milk-desk-1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/desk/hero-milk-desk-2x.png);
