  font-style: normal;
  font-weight: normal;
  font-size: 26px;
  line-height: 1.15;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffffff;
  margin-bottom: 19px;
  @media screen and (min-width: 480px) {
    font-size: 26px;
    line-height: 30px;
  }
  @media screen and (min-width: 768px) {
    font-size: 22px;
    line-height: 1.14;
    height: 80px;
  }
  @media screen and (min-width: 1280px) {
    font-size: 38px;
    line-height: 1.16;
    margin-bottom: 22px;
    height: 137px;
  }
}

.hero__passion {
  font-family: Titan One;
  font-size: 48px;
  font-style: normal;
  font-weight: 400;
  line-height: 46px;
  letter-spacing: 0.04em;
  text-align: left;
  color: $brand-color;
  @media screen and (min-width: 768px) {
    font-size: 34px;
    line-height: 0.9em;
  }
  @media screen and (min-width: 1280px) {
    font-size: 58px;
    line-height: 0.7em;
  }
}

.button {
  padding: 8px 13px 8px 13px;
  border-radius: 20px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: normal;
  font-size: 8px;
  line-height: 1.25;
  border: none;
  margin-bottom: 66px;
  @media screen and (min-width: 1280px) {
    font-size: 14px;
    line-height: 1.3;
    padding: 11px 20px 11px 20px;
    margin-bottom: 112px;
  }
}

.hero__container-btn {
  height: 24px;
}
.hero__container-products-btn {
  background-color: $brand-color;
  color: #ffffff;
  margin-right: 8px;
  &:hover,
  &:focus {
    background-color: #c7133e;
  }
  margin-bottom: 0;
}

.hero__container-him-btn {
  background-color: #ffffff;
  color: $brand-color;
  transition: color, 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    background-color: #ecebeb;
  }
  margin-bottom: 0;
}

.hero__container-btn--link-how {
  color: #d41443;
}
.hero__container-btn--link-prod {
  color: #ffffff;
}

