sources.sass\\_products.scss:
.product-section {
  padding-top: 127px;
}

.poduct-container {
  @include tab {
    padding-left: 34px;
    padding-right: 34px;
  }
  @media screen and (max-width: 767px) {
    width: 100%;
    max-width: 480px;
  }
  @media screen and (min-width: 768px) {
    width: 100%;
    max-width: 768px;
  }
  @media screen and (min-width: 1200px) {
    width: 100%;
    max-width: 1200px;
  }

  margin-left: auto;
  margin-right: auto;
  padding-left: 20px;
  padding-right: 20px;
}

.product-section-title {
  font-family: $font-family-title;
  font-size: 18px;
  line-height: 1.16;
  text-align: center;
  text-transform: uppercase;
  color: #ffa5ba;
  display: block;
  @include tab {
    font-size: 26px;
    line-height: 1.15;
  }
  @include desk {
    font-size: 30px;
    line-height: 1.13;
  }
}

.product-subtitle {
  font-family: $font-family-title;
  font-size: 34px;
  line-height: 1.14;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #d41443;
  margin-bottom: 144px;
  @include tab {
    margin-bottom: 150px;
    font-size: 48px;
    line-height: 1.14;
  }
  @include desk {
    margin-bottom: 259px;
    font-size: 58px;
    line-height: 1.13;
  }
}

.product__list {
  @include tab {
    display: flex;
    flex-wrap: wrap;
    margin-left: -20px;
    margin-top: -20px;
  }
  @include desk {
    margin-left: -30px;
    margin-top: -30px;
  }
  text-align: center;
}

.product__item {
  @include tab {
    flex-basis: calc(100% / 3 - 20px);
    margin-left: 20px;
    margin-top: 20px;
  }
  @include tab {
    min-width: 220px;
  }
  @include desk {
    max-width: 330px;
    height: 525px;
    margin-left: 30px;
    margin-top: 30px;
  }
  position: relative;
  display: inline-block;
  width: 280px;
  height: 485px;
  margin-bottom: 130px;
  text-align: center;
}
.product-card1 {
  background-color: $subtitle-color;
  border-radius: 24px;
}

.product-card2 {
  background-color: #f0d1a5;
  border-radius: 24px;
}
.product-card3 {
  background-color: #c2e297;
  border-radius: 24px;
}

.product-card1::before {
  content: '';
  position: absolute;
  top: -113px;
  left: 16px;
  width: 249px;
  height: 252px;
  background-repeat: no-repeat;
