  }
  @keyframes AnimationName {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
}
.about__modal--title {
  color: #fff;
  font-family: 'Titan One';
  font-style: normal;
  font-weight: 400;
  font-size: 28px;
  line-height: 1.14;
  text-align: center;
  text-transform: uppercase;
  margin-top: 10px;
  @media screen and(min-width:768px) {
    font-size: 34px;
    line-height: 1.14;
  }
}
.about__modal--text {
  color: #ffa5ba;
  font-family: 'Titan One';
  font-style: normal;
  font-size: 15px;
  line-height: 1.13;
  text-align: left;
  text-transform: uppercase;
  padding: 15px 15px 20px 15px;
  @media screen and(min-width:768px) {
    font-size: 18px;
    line-height: 1.16;
  }
}
.about__modal--close {
  font-family: 'Titan One';
  height: 44px;
  width: 176px;
  background-color: #fff;
  color: #ffa5ba;
  cursor: pointer;
  border-radius: 22px;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31px;
  letter-spacing: 0.06em;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
  border-color: transparent;
  &:hover,
  &:focus {
    background-color: rgb(194, 235, 203);
    color: #fff;
  }
}
.about__modal--picture {
  margin-top: 5px;
  display: inline-block;
}

