  .slider__box--second {
    background-image: url(../images/general/testimonials--second--640.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
  }
  .slider__box--third {
    background-image: url(../images/general/testimonials--third--640.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
  }
}
@media screen and (min-device-pixel-ratio: 3),
  screen and (min-resolution: 3dppx),
  screen and (min-resolution: 288dpi) {
  .slider__box--first {
    background-image: url(../images/general/testimonials--first--960.jpg);
    background-size: cover;
